function HTMLize(spellObj) {
    let obj = spellObj;
    let mInit = `<div class="materiales tipo"><div>materiales</div><p>`;
    let mEnd = `</p></div>`;
    let html = [
        `<div class="conjuro">`,
            `<header class="encabezado">`,
                `<div class="titulo">`,
                    `<h3>${obj.name}</h3>`, 
                    `<p>${(obj.level == "Truco")? obj.level: "Nivel " + obj.level}</p>`,
                `</div>`,
                `<p>${obj.school} ${obj.ifRitual? "(ritual)": ""}</p>`,
            `</header>`,
            `<main class="cuerpo">`,
                `<div class="tiempo-de-conjuración tipo">`,
                    `<div>tiempo de conjuración</div>`,
                    `<p>${obj.launchTime}</p>`,
                `</div>`,
                `<div class="componentes tipo">`,
                    `<div>componentes:</div>`,
                    `${obj.components.replace(/[A-Za-záó]+/g, a => `<p>${a}</p>`)}`,
                `</div>`,
                ` ${obj.materials != null? mInit + obj.materials + mEnd:""} `,
                `<div class="duración tipo">`,
                    `<div>Duracion</div>`,
                    `<p>${obj.duration}</p>`,
                `</div>`,
                `<div class="alcance tipo">`,
                    `<div>alcance</div>`,
                    `<p>${obj.scope}</p>`,
                `</div>`,
                `<div class="descripción">${obj.description}</div>`,
            `</main>`,
        `</div>`
    ];
    html = html.join('');
    return html;
}

